mapper.update:
UPDATE qna
SET title = #{title} , content = #{content}
WHERE no = #{no}

UPDATE qna
SET masterid = #{masterid} , anscontent = #{anscontent} ,anstime = now()
WHERE no = #{no}

mapper.select:
SELECT *
FROM qna
where userid=#{userid}
order by asktime desc
limit #{limit} offset #{offset}

SELECT *
FROM qna
where masterid IS NOT NULL and userid=#{userid}
order by asktime desc
limit #{limit} offset #{offset}

mapper.update:
update qna
set hit = hit + 1
where no = #{no}
